import{_ as e,c as t,o as r,am as i}from"./chunks/framework.Cl52jaxg.js";const m=JSON.parse('{"title":"浏览器查看","description":"通过浏览器查看环境可视化的内容","frontmatter":{"title":"浏览器查看","description":"通过浏览器查看环境可视化的内容","layout":"doc"},"headers":[],"relativePath":"version/v2/docs/advanced/mobile/browser.md","filePath":"version/v2/docs/advanced/mobile/browser.md"}'),l={name:"version/v2/docs/advanced/mobile/browser.md"};function o(n,a,s,c,d,h){return r(),t("div",null,[...a[0]||(a[0]=[i('<h1 id="浏览器查看" tabindex="-1">浏览器查看 <a class="header-anchor" href="#浏览器查看" aria-label="Permalink to “浏览器查看”">​</a></h1><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to “简介”">​</a></h2><p>如果你只是想在其他的设备上查看环境可视化内部的话，那么该方法非常适合你。</p><h2 id="准备工作" tabindex="-1">准备工作 <a class="header-anchor" href="#准备工作" aria-label="Permalink to “准备工作”">​</a></h2><p>一台移动设备（不管是手机还是平板，当然平板更好，更方便操作）。 火狐浏览器（非必须，你仍然可以使用设备上自带的浏览器，但是你需要确保他不会将http协议自动转换为https，否则你仍然需要使用火狐/firefox浏览器）。</p><h2 id="开始" tabindex="-1">开始 <a class="header-anchor" href="#开始" aria-label="Permalink to “开始”">​</a></h2><ol><li>在你的电脑上打开ETS2LA并启用可视化插件。</li><li>进入游戏并确保游戏处于驾驶中而非菜单页面。</li><li>打开ETS2LA的设置-可视化-其他设备，你应该能看到这样的页面，如果不是，请确保你已在设置中开启可视化插件。 <img src="https://tc.ets2la.cn/d/img/25-12/20250819090706.png" alt="" loading="lazy"> 在其他设备访问 <a href="http://visualization.ets2la.com" target="_blank" rel="noreferrer">http://visualization.ets2la.com</a> 或者 <a href="http://visualization.ets2la.cn" target="_blank" rel="noreferrer">http://visualization.ets2la.cn</a> 这两个网站中的任意一个，你看到的应该是这样的页面。 <img src="https://tc.ets2la.cn/d/img/25-12/72b22c7914f28c2502e254303365f352.jpeg" alt="" loading="lazy"> 不要慌张，将从ETS2LA设置中看到的ip信息（192.168开头的，我这里是192.168.3.8:37522）填入到方框中（保留开头的ws://），然后点击 <code>Retry Connection</code>，即可通过手机/平板查看环境可视化内部，像这样。 <img src="https://tc.ets2la.cn/d/img/25-12/20250819091206.png" alt="" loading="lazy"></li></ol>',7)])])}const _=e(l,[["render",o]]);export{m as __pageData,_ as default};
